document.addEventListener("DOMContentLoaded",event=>{const navbar=document.getElementById("fixed-menu-logo"),navbarActions=document.getElementById("navbar-actions"),stickyButton=document.getElementById("sticky-button");window.addEventListener("scroll",()=>{document.documentElement.scrollTop>150?(navbar.classList.remove("logo--hidden"),navbarActions.classList.add("hidden--mobile"),stickyButton.classList.remove("hidden--desktop")):(navbarActions.classList.remove("hidden--mobile"),navbar.classList.add("logo--hidden"),stickyButton.classList.add("hidden--desktop"))})});